        .af-table th, .af-table td { padding:6px 8px; vertical-align:middle; }
        .af-table input, .af-table select, .af-table textarea { font-size:14px; border:1px solid #d7dbe0; border-radius:6px; background:#fff; }
        .af-table input, .af-table select { height:32px; line-height:32px; padding:4px 8px; }
        .af-table textarea { padding:6px 8px; }

  .ph-btn { cursor:pointer; }
  .profile-overlay { backdrop-filter: blur(2px); background: rgba(2,6,23,0.35); display:flex; align-items:center; justify-content:center; position:fixed; inset:0; z-index:1200; }
  .profile-modal { box-shadow: 0 10px 30px rgba(2,6,23,0.12); border: 1px solid rgba(2,6,23,0.06); }
  .profile-modal__form { grid-template-columns: repeat(2, minmax(180px,1fr)); }
  .profile-modal__form label { min-width:0; }
  .profile-modal__form input, .profile-modal__form select, .profile-modal__form textarea { box-sizing: border-box; width:100%; min-width:0; }
  @media (max-width:720px){ .profile-modal__form { grid-template-columns: 1fr; } }
