// Centralized asset path mapping + fallback helper
// After you physically move/copy images into /public/assets/** structure
// components can import from here instead of hard-coding /icon-*.png

const BASE = '/assets';

export const ASSET_PATHS = {
  icons: {
    ui: {
      user: `${BASE}/icons/ui/icon-user.png`,
      grid: `${BASE}/icons/ui/icon-grid.png`,
      bell: `${BASE}/icons/ui/icon-bell.png`,
      search: `${BASE}/icons/ui/icon-search.png`,
      language: `${BASE}/icons/ui/icon-language.png`,
      schedule: `${BASE}/icons/ui/icon-schedule.png`,
      speaker: `${BASE}/icons/ui/icon-speaker.png`,
      check: `${BASE}/icons/ui/icon-check.svg`,
    },
    social: {
      facebook: `${BASE}/icons/social/icon-facebook.png`,
      twitter: `${BASE}/icons/social/icon-twitter.png`,
      instagram: `${BASE}/icons/social/icon-instagram.png`,
    },
    contact: {
      location: `${BASE}/icons/contact/icon-location.png`,
      phone: `${BASE}/icons/contact/icon-phone.png`,
      mail: `${BASE}/icons/contact/icon-mail.png`,
    },
    profile: {
      user: `${BASE}/icons/profile/icon-user.png`, // duplicate as requested
    }
  },
  branding: {
    logo: `${BASE}/branding/logo.png`
  },
  general: {
    chatbox: `${BASE}/general/chatbox.png`,
    google: `${BASE}/general/google_login.png`,
    qr: `${BASE}/general/qr-image.jpg`,
    replace: `${BASE}/general/replace.png`,
    success: `${BASE}/general/success.png`
  },
  // keep original folders for rooms & banners (no move to avoid SQL path changes)
  legacy: {
    hangphong: '/hangphong',
    khachsan: '/khachsan',
    homeBanner: '/HomeBanner'
  }
};

// Fallback: if new path 404 (not yet moved), revert to old root-level file name deduced from path
export function withFallback(newPath) {
  const oldName = '/' + (newPath.split('/').pop() || '');
  return {
    src: newPath,
    onError: (e) => {
      if (!e.target.dataset.fb) {
        e.target.dataset.fb = '1';
        e.target.src = oldName; // fallback to original root placement
      }
    }
  };
}

// Convenience wrappers
export const Icon = {
  user: () => withFallback(ASSET_PATHS.icons.ui.user),
  grid: () => withFallback(ASSET_PATHS.icons.ui.grid),
  bell: () => withFallback(ASSET_PATHS.icons.ui.bell),
  search: () => withFallback(ASSET_PATHS.icons.ui.search),
  language: () => withFallback(ASSET_PATHS.icons.ui.language),
  schedule: () => withFallback(ASSET_PATHS.icons.ui.schedule),
  speaker: () => withFallback(ASSET_PATHS.icons.ui.speaker),
  check: () => withFallback(ASSET_PATHS.icons.ui.check),
  facebook: () => withFallback(ASSET_PATHS.icons.social.facebook),
  twitter: () => withFallback(ASSET_PATHS.icons.social.twitter),
  instagram: () => withFallback(ASSET_PATHS.icons.social.instagram),
  location: () => withFallback(ASSET_PATHS.icons.contact.location),
  phone: () => withFallback(ASSET_PATHS.icons.contact.phone),
  mail: () => withFallback(ASSET_PATHS.icons.contact.mail),
  profileUser: () => withFallback(ASSET_PATHS.icons.profile.user)
};

export const Branding = {
  logo: () => withFallback(ASSET_PATHS.branding.logo)
};

export const General = {
  chatbox: () => withFallback(ASSET_PATHS.general.chatbox),
  google: () => withFallback(ASSET_PATHS.general.google),
  qr: () => withFallback(ASSET_PATHS.general.qr),
  replace: () => withFallback(ASSET_PATHS.general.replace),
  success: () => withFallback(ASSET_PATHS.general.success)
};
