      const rooms = data && Array.isArray(data.rooms) ? data.rooms : [];
      const headerText = rooms.length
        ? `CÃ³ ${rooms.length} phÃ²ng thuá»™c háº¡ng ${info.name}${info.hotelName ? ` táº¡i ${info.hotelName}` : ''}.`
        : `Hiá»‡n chÆ°a cÃ³ phÃ²ng kháº£ dá»¥ng cho háº¡ng ${info.name}.`;
      const followUps = [
        { role: 'bot', text: headerText },
        rooms.length ? {
          role: ROOM_LIST_ROLE,
          items: rooms.map(r => ({
            id: r.id,
            roomNumber: r.roomNumber,
