      ].filter(Boolean);
      setMessages(prev => [...prev, ...followUps]);
    } catch {
      setMessages(prev => [...prev, { role: 'bot', text: 'KhÃ´ng táº£i Ä‘Æ°á»£c danh sÃ¡ch phÃ²ng. Vui lÃ²ng thá»­ láº¡i sau.' }]);
