        { keywords:['má»Ÿ phÃ²ng','open rooms','open room list','má»Ÿ danh sÃ¡ch phÃ²ng'], event:'open-rooms' },
        { keywords:['má»Ÿ tiá»‡n nghi','open amenities','open amenity'], event:'open-amenities' },
        { keywords:['má»Ÿ khuyáº¿n mÃ£i','má»Ÿ Æ°u Ä‘Ã£i','open promotions','open promotion'], event:'open-promotions' },
        { keywords:['má»Ÿ Ä‘Ã¡nh giÃ¡','open reviews','xem Ä‘Ã¡nh giÃ¡'], event:'open-reviews' },
        { keywords:['má»Ÿ Ä‘áº·t phÃ²ng','Ä‘áº·t phÃ²ng ngay','booking now','book now'], event:'open-booking' },
        { keywords:['má»Ÿ há»“ sÆ¡','má»Ÿ profile','open profile','thÃ´ng tin cÃ¡ nhÃ¢n'], event:'open-profile' },
        { keywords:['má»Ÿ thanh toÃ¡n','xem thanh toÃ¡n','payment history','má»Ÿ lá»‹ch sá»­ giao dá»‹ch','xem lá»‹ch sá»­ giao dá»‹ch','lá»‹ch sá»­ giao dá»‹ch','má»Ÿ lá»‹ch sá»­ thanh toÃ¡n','xem lá»‹ch sá»­ thanh toÃ¡n'], event:'open-payments' },
        { keywords:['má»Ÿ thÃ´ng bÃ¡o','xem thÃ´ng bÃ¡o','open notifications'], event:'open-notifications' },
      ];
      const found = commandMap.find(c => c.keywords.some(k => lower.includes(k)));
      if (found) {
        fire(found.event, { source:'chatbot', original: msg });
        // Provide a local immediate bot acknowledgement without calling API
        setMessages(m => [...m, { role:'bot', text: 'Äang má»Ÿ giao diá»‡n theo yÃªu cáº§u cá»§a báº¡n...' }]);
        return; // skip server request for pure UI command
      }
    } catch {/* ignore command parse errors */}

    setLoading(true);
