        @media (max-width: 1050px){
          .af-table thead th:nth-child(3), .af-table tbody td:nth-child(3){ max-width:220px; }
          .af-toolbar { gap:12px; }
        }
        @media (max-width: 820px){
          .af-table thead th:nth-child(5), .af-table tbody td:nth-child(5){ display:none; }
