      const imgsRaw = Array.isArray(rf?.images || r.images)? (rf?.images || r.images) : [];
      const imgs = imgsRaw.map(im=> normalizeImage(im)).filter(Boolean);
      const merged = {
        ...r,
        ...rf,
        images: imgs,
        image: imgs[0] || r.image,
