      if (!q || q.toLowerCase() !== targetName) {
        setQ(detail.name);
      }
    }
    setPendingBooking(null);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [pendingBooking, rooms]);

  // ESC key to close detail modal
  useEffect(()=>{
