    }
  }, [bookingRoom?.hotelId]);

  function flashTotal() {
    try {
      const els = [];
      const a = document.querySelectorAll('.rb-booking-summary span:last-child, .rb-booking-summary strong');
      a.forEach(x => els.push(x));
      if (!els.length) return;
